INSERT INTO USUARIOS (usuario, contraseña, email) VALUES
    ('hector', '1234', 'hector@gmail.com');
INSERT INTO Videojuegos (nombre, portada, precio, descripcion) VALUES
    ('Spiderman 2 PS5', 'spiderman2.jpg', 79, '¡Siente el poderío arácnido en esta emocionante secuela donde Spiderman se enfrenta a nuevos desafíos y enemigos en la ciudad de Nueva York.'),
    ('God of War Ragnarok PS5', 'gowr.jpg', 79, 'Únete a Kratos en una nueva aventura épica donde se enfrenta a los dioses nórdicos en un viaje para salvar a su hijo y detener el Ragnarok.'),
    ('StellarBlade PS5', 'StellarBlade.jpg', 79, 'Explora un vasto universo lleno de peligros y maravillas en esta odisea espacial llena de acción y misterio.'),
    ('The Legend of Zelda: Breath of the Wild Switch', 'botw.jpg', 69, 'Embárcate en una épica aventura en el vasto reino de Hyrule, donde la libertad de exploración y la naturaleza son tus mayores aliados contra la oscuridad.'),
    ('The Last of Us 2 Remastered PS5', 'tlou2.jpg', 49, 'Sobrevive en un mundo postapocalíptico brutal mientras Ellie busca venganza en una emocionante historia de amor, pérdida y redención.'),
    ('Persona 3 Reload PS5', 'p3r.jpg', 69, 'Únete al Grupo Especializado en Exterminio y lucha contra las sombras mientras exploras los oscuros secretos de la mente humana en este clásico JRPG.'),
    ('Ghost of Tsushima PS5', 'tsushima.jpg', 79, 'Sumérgete en la piel de un samurái en esta épica aventura de mundo abierto ambientada en el Japón feudal, donde el honor y la espada son tus armas.'),
    ('The Last of Us Remake PS5', 'tlou.jpg', 79, 'Revive la experiencia emocionalmente impactante de Joel y Ellie en un mundo devastado por la pandemia, ahora reimaginado para la potencia de la PlayStation 5.'),
    ('Horizon Forbidden West PS5', 'horizon.jpg', 79, 'Explora un mundo postapocalíptico rebosante de vida y peligro en esta secuela de Horizon Zero Dawn, donde Aloy se embarca en una misión para salvar su mundo.'),
    ('Gran Turismo 7 PS5', 'gt7.jpg', 79, 'Experimenta la emoción de la velocidad y la precisión en este simulador de conducción de renombre mundial, donde cada curva y cada coche es una obra maestra de la ingeniería.'),
    ('Paper Mario: The Origami King', 'papermario.jpg', 59, 'Únete a Paper Mario en una colorida aventura llena de humor y rompecabezas mientras lucha contra el malvado Rey Olly y su ejército de origami.'),
    ('Super Mario Odyssey', 'odissey.jpg', 59, 'Embárcate en una emocionante búsqueda por diferentes reinos con Mario y Cappy en busca de la princesa Peach, ¡y diviértete con nuevos movimientos y transformaciones!'),
    ('The Legend of Zelda: Tears of the Kingdom Switch', 'totk.jpg', 59, 'Únete a Link en una nueva aventura donde debe salvar el reino de Hyrule de una misteriosa maldición que hace llorar a sus habitantes, enfrentando nuevos desafíos y mazmorras.'),
    ('Pokémon Espada Switch', 'espada.jpg', 59, 'Explora la región de Galar y conviértete en el campeón de la Liga Pokémon mientras capturas y entrenas a poderosos Pokémon espada en este emocionante juego de rol.'),
    ('Pokémon Escudo Switch', 'escudo.jpg', 59, 'Embárcate en una emocionante aventura en la región de Galar mientras te conviertes en el campeón de la Liga Pokémon y defiendes la región con poderosos Pokémon escudo.'),
    ('Xenoblade Chronicles Remastered Switch', 'xenoblade.jpg', 59, 'Descubre la verdad detrás del mundo titánico de Bionis y Mechonis mientras te unes a Shulk y sus amigos en una emocionante aventura llena de combate estratégico y emocionantes giros.'),
    ('Xenoblade Chronicles 2 Switch', 'xenoblade2.jpg', 59, 'Explora el mundo de Alrest en busca del legendario Paraíso Elysium junto a Rex y Pyra en este emocionante juego de rol lleno de personajes entrañables y combates épicos.'),
    ('Xenoblade Chronicles 3 Switch', 'xenoblade3.jpg', 59, 'Sumérgete en una nueva historia en el universo de Xenoblade mientras te unes a un nuevo elenco de personajes en una aventura épica llena de misterio, traición y descubrimientos impactantes.'),
    ('Super Mario 3D World', 'mario3d.jpg', 59, 'Únete a Mario, Luigi, Peach y Toad en una emocionante aventura para salvar el Reino de las hadas de las garras de Bowser, ¡con nuevos power-ups y la posibilidad de jugar en multijugador!'),
    ('Mario Kart 8 Deluxe', 'mariokart.jpg', 59, 'Corre a toda velocidad en emocionantes pistas con tus personajes favoritos de Nintendo en karts personalizables, ¡y disfruta de nuevos circuitos y personajes en esta versión definitiva de Mario Kart 8!'),
    ('Super Smash Bros. Ultimate', 'smash.jpg', 59, 'Reúne a tus personajes favoritos de videojuegos en el crossover definitivo y lucha en emocionantes combates en una variedad de escenarios, ¡con nuevos luchadores y modos de juego!'),
    ('Starfield Xbox', 'starfield.jpg', 79, 'Explora el vasto espacio en busca de tu lugar en el cosmos en este juego de rol de ciencia ficción épico de los creadores de The Elder Scrolls y Fallout.'),
    ('Microsfot Fly Simulator Xbox', 'flysim.jpg', 79, 'Experimenta la emoción de volar sobre paisajes detallados de todo el mundo en este simulador de vuelo realista y asombroso, ¡con gráficos de última generación y una física precisa!'),
    ('Forza Horizon 5 Xbox', 'forzah.jpg', 79, 'Embárcate en un emocionante viaje por los hermosos paisajes de México en este juego de carreras de mundo abierto, ¡con una gran variedad de vehículos y eventos para disfrutar!'),
    ('Forza Motorsport 7 Xbox', 'forzam.jpg', 79, 'Siente la emoción de la competición en este realista simulador de carreras donde podrás conducir una amplia gama de vehículos en circuitos de todo el mundo, ¡con gráficos impresionantes y física precisa!'),
    ('Halo Infinite Xbox', 'halo.jpg', 79, '¡Únete al Jefe Maestro en su última misión para salvar la humanidad en un vasto anillo Halo lleno de peligros y misterios, con un nuevo enfoque en la exploración y la libertad de juego!'),
    ('Star Wars Jedi: Survivor Xbox', 'jedi.jpg', 79, 'Sobrevive como un Jedi perdido en un mundo hostil tras la caída de la Orden Jedi, aprende nuevas habilidades de la Fuerza y construye tu sable láser en esta emocionante aventura de Star Wars.'),
    ('Star Wars Jedi: Survivor PS5', 'jedips5.jpg', 79, 'Sobrevive como un Jedi perdido en un mundo hostil tras la caída de la Orden Jedi, aprende nuevas habilidades de la Fuerza y construye tu sable láser en esta emocionante aventura de Star Wars.'),
    ('Alan Wake 2 PS5', 'alanwake2.jpg', 59, 'Sumérgete en un mundo de oscuridad y misterio en esta secuela del aclamado thriller psicológico, donde Alan Wake regresa para enfrentarse a nuevas pesadillas y secretos del pasado.'),
    ('Final Fantasy 16 PS5', 'ff16.jpg', 79, 'Embárcate en una épica aventura de fantasía en el mundo de Valisthea, donde la magia y la tecnología se enfrentan en una lucha por el destino del reino, con personajes inolvidables y emocionantes batallas.'),
    ('Dying Light 2 PS5', 'dyinglight2.jpg', 69, 'Sobrevive en un mundo postapocalíptico infestado de zombis y facciones rivales mientras tomas decisiones que afectarán el destino de la ciudad, en esta secuela del exitoso juego de supervivencia y parkour.'),
    ('Death Stranding PS5', 'deathstranding.jpg', 69, 'Explora un mundo distópico donde la conexión entre las personas es crucial para la supervivencia, en esta obra maestra de Hideo Kojima que desafía los límites del género de mundo abierto y la narrativa interactiva.'),
    ('Forspoken PS5', 'forspoken.jpg', 79, 'Descubre un mundo de magia y misterio en la piel de Frey Holland, una joven que es transportada a un reino fantástico donde deberá dominar poderes mágicos para enfrentarse a peligrosas criaturas y desentrañar antiguos secretos.'),
    ('Final Fantasy 7 Rebirth PS5', 'ff7.jpg', 79, 'Revive la leyenda de Final Fantasy 7 en una nueva aventura que expande el universo del juego original con personajes familiares y nuevos desafíos, en este esperado título que mezcla nostalgia con innovación.'),
    ('Final Fantasy 7 Remake PS5', 'ff7remake.jpg', 79, 'Revive la legendaria historia de Cloud Strife y su lucha contra Shinra en la ciudad de Midgar, con gráficos impresionantes, combates emocionantes y una narrativa emocionalmente impactante.'),
    ('Soul Hackers 2 PS5', 'soulhackers2.jpg', 69, 'Adéntrate en un mundo cibernético lleno de peligros y secretos en esta secuela del clásico JRPG, donde los hackers luchan por el control de la red mientras descubren oscuros secretos del pasado.'),
    ('A Plague Tale Requiem Xbox', 'plague.jpg', 69, 'Sobrevive a una Europa medieval devastada por la peste en esta secuela del aclamado juego de aventuras y sigilo, donde Amicia y Hugo deben enfrentarse a nuevas amenazas y desafíos en su lucha por la supervivencia.'),
    ('Cyberpunk 2077 Ultimate Edition PS5', 'cyberpunk.jpg', 69, 'Adéntrate en Night City, la metrópolis del futuro obsesionada con el poder, la moda y la tecnología, en este RPG de mundo abierto lleno de acción y decisiones morales que afectarán el destino de la ciudad y sus habitantes.'),
    ('The Calisto Protocol Xbox', 'calisto.jpg', 69, 'Sobrevive en una prisión lunar infestada de horrores en esta nueva IP de los creadores de Dead Space, donde la oscuridad y la locura acechan en cada rincón, en una experiencia de terror visceral y claustrofóbica.'),
    ('Demon Souls Remake PS5', 'demonsouls.jpg', 79, 'Revive la experiencia brutal y desafiante del clásico juego de FromSoftware, completamente reconstruido para la PlayStation 5 con gráficos impresionantes y un combate renovado, en un viaje a través de la oscuridad y el peligro.'),
    ('Persona 5 Royal PS5', 'persona5ps5.jpg', 69, 'Únete a los Ladrones Fantasma en su lucha contra la corrupción en Tokio en esta versión mejorada y expandida del aclamado JRPG, con nuevos personajes, mazmorras y eventos que profundizan en la historia y los lazos emocionales.'),
    ('Persona 5 Royal Switch', 'persona5.jpg', 69, 'Únete a los Ladrones Fantasma en su lucha contra la corrupción en Tokio en esta versión mejorada y expandida del aclamado JRPG, con nuevos personajes, mazmorras y eventos que profundizan en la historia y los lazos emocionales.'),
    ('FarCry 6 Xbox', 'fc6.jpg', 69, 'Embárcate en una peligrosa aventura en la isla de Yara, bajo el yugo de un dictador despiadado, en este juego de acción de mundo abierto lleno de combate frenético, exploración y decisiones morales.'),
    ('FarCry 6 PS5', 'fc6ps5.jpg', 69, 'Embárcate en una peligrosa aventura en la isla de Yara, bajo el yugo de un dictador despiadado, en este juego de acción de mundo abierto lleno de combate frenético, exploración y decisiones morales.'),
    ('Scarlet Nexus Xbox', 'scarlet.jpg', 69, 'Descubre un mundo de psiónicos y monstruos en esta nueva IP de Bandai Namco, donde controlarás a Yuito o Kasane en una emocionante aventura llena de combates frenéticos y una historia emocionante.'),
    ('Yakuza: Like a Dragon Xbox', 'yakuza.jpg', 69, 'Únete a Ichiban Kasuga en su búsqueda de venganza y redención en Yokohama, en este emocionante capítulo de la saga Yakuza que combina combates por turnos y una historia emocionalmente impactante.'),
    ('Like a Dragon: Infinite Wealth Xbox', 'yakuza2.jpg', 69, 'Únete a Ichiban Kasuga en su búsqueda de venganza y redención en Yokohama, en este emocionante capítulo de la saga Yakuza que combina combates por turnos y una historia emocionalmente impactante.'),
    ('Yakuza: Like a Dragon PS5', 'yakuzaps5.jpg', 69, 'Únete a Ichiban Kasuga en su búsqueda de venganza y redención en Yokohama, en este emocionante capítulo de la saga Yakuza que combina combates por turnos y una historia emocionalmente impactante.'),
    ('Like a Dragon: Infinite Wealth PS5', 'yakuza2ps5.jpg', 69, 'Únete a Ichiban Kasuga en su búsqueda de venganza y redención en Yokohama, en este emocionante capítulo de la saga Yakuza que combina combates por turnos y una historia emocionalmente impactante.'),
    ('Rise of The Ronin PS5', 'ronin.jpg', 79, 'Embárcate en una épica aventura samurái en el Japón feudal, donde te convertirás en un ronin en busca de venganza y redención, en un mundo lleno de peligros y desafíos.'),
    ('Tekken 8 Xbox', 'tekken8.jpg', 79, 'Regresa al torneo King of Iron Fist en esta nueva entrega de la icónica saga de lucha, donde los luchadores más habilidosos del mundo se enfrentan en combates intensos y emocionantes, ¡con nuevos personajes y mecánicas de juego!'),
    ('Resident Evil 4 Remake PS5', 're4.jpg', 79, 'Revive la pesadilla de Leon Kennedy en su misión para rescatar a la hija del presidente en un oscuro pueblo de Europa, en esta versión completamente reconstruida y mejorada de uno de los mejores juegos de la saga.'),
    ('Dead Island 2 Xbox', 'di2.jpg', 79, 'Sobrevive al apocalipsis zombi en el soleado estado de California en este juego de mundo abierto lleno de acción y supervivencia, donde podrás enfrentarte a hordas de no muertos con un arsenal de armas improvisadas y habilidades únicas.'),
    ('Dead Island 2 PS5', 'di2ps5.jpg', 79, 'Sobrevive al apocalipsis zombi en el soleado estado de California en este juego de mundo abierto lleno de acción y supervivencia, donde podrás enfrentarte a hordas de no muertos con un arsenal de armas improvisadas y habilidades únicas.'),
    ('Elden Ring Xbox', 'eldenring.jpg', 79, 'Descubre un mundo de fantasía oscura y misterio en esta colaboración entre George R.R. Martin y FromSoftware, donde explorarás un vasto reino lleno de peligros y secretos, en una aventura épica llena de desafíos.'),
    ('Elden Ring PS5', 'eldenringps5.jpg', 79, 'Descubre un mundo de fantasía oscura y misterio en esta colaboración entre George R.R. Martin y FromSoftware, donde explorarás un vasto reino lleno de peligros y secretos, en una aventura épica llena de desafíos.'),
    ('Super Mario Bros Wonder Switch', 'wonder.jpg', 59, 'Embárcate en una nueva aventura en el Reino Champiñón mientras Mario y Luigi deben salvar a la Princesa Peach de las garras de Bowser en este emocionante juego de plataformas lleno de acción y diversión.'),
    ('Metroid Dread Switch', 'metroiddread.jpg', 59, 'Únete a Samus Aran en su última misión para detener una amenaza alienígena en el planeta ZDR, en este juego de acción y exploración que marca el regreso triunfal de la saga Metroid a las consolas.'),
    ('Metroid Prime Remastered Switch', 'metroidprime.jpg', 59, 'Revive la experiencia épica de Metroid Prime en esta versión remasterizada con gráficos mejorados y controles optimizados, en una aventura que te llevará a explorar mundos alienígenas llenos de misterios y peligros.'),
    ('Ori The Collection Switch', 'ori.jpg', 59, 'Embárcate en una emotiva aventura en los bosques de Nibel y Niwen en este recopilatorio que incluye Ori and the Blind Forest y Ori and the Will of the Wisps, en una experiencia de plataformas y exploración visualmente deslumbrante.'),
    ('Bayonetta 3 Switch', 'bayonetta3.jpg', 59, 'Regresa a la acción con la bruja Umbra en esta esperada tercera entrega de la saga Bayonetta, donde enfrentarás nuevos enemigos y desafíos en emocionantes combates llenos de estilo y poderes mágicos.'),
    ('Bayonetta + Bayonetta 2 Switch', 'bayonetta2.jpg', 59, 'Experimenta la acción desenfrenada de Bayonetta y Bayonetta 2 en esta colección que reúne dos de los mejores juegos de acción y hack and slash de la última década, ¡con gráficos mejorados y todos los DLC incluidos!'),
    ('Animal Crossing New Horizons Switch', 'animalcrossing.jpg', 59, 'Crea tu propio paraíso en una isla desierta en este juego relajante y adictivo, donde podrás construir, decorar y socializar con tus vecinos en un mundo lleno de encanto y diversión.'),
    ('Nier Automata Switch', 'nier.jpg', 59, 'Descubre una emocionante historia de androides y máquinas en este juego de acción y RPG que desafía los límites del género, con combates fluidos, una narrativa profunda y una banda sonora inolvidable.'),
    ('Monster Hunter Rise + Sunbreak Switch', 'rise.jpg', 59, 'Únete a la caza en un mundo lleno de monstruos y peligros en Monster Hunter Rise, y prepárate para la expansión Sunbreak que añade nuevos monstruos, áreas y desafíos en esta épica aventura de caza.'),
    ('The Legend of Zelda: Links Awakening Switch', 'zelda.jpg', 59, 'Revive la clásica aventura de Link en la misteriosa isla de Koholint en esta versión remasterizada del clásico de Game Boy, con gráficos renovados, mecánicas de juego mejoradas y nuevos desafíos.'),
    ('Astral Chain Switch', 'astralchain.jpg', 59, 'Únete a la Fuerza de Policía Especial Neurón y lucha contra una invasión interdimensional en esta nueva IP de PlatinumGames, donde controlarás a dos personajes a la vez en combates llenos de acción y estilo.'),
    ('Luigis Mansion 3 Switch', 'luigis.jpg', 59, 'Ayuda a Luigi a rescatar a sus amigos de las garras de los fantasmas en el lúgubre Hotel Last Resort, en esta aventura llena de puzles, humor y sorpresas, ¡con gráficos impresionantes y una jugabilidad divertida!'),
    ('Mario + Rabidds Sparks of Hope Switch', 'rabidds.jpg', 59, 'Únete a Mario, los Rabbids y nuevos héroes en una misión intergaláctica para salvar el universo de una oscura amenaza, en este juego de estrategia y acción lleno de humor y desafíos tácticos.'),
    ('Uncharted: Colección Legado de los Ladrones PS5', 'uncharted.jpg', 79, 'Revive las aventuras de Nathan Drake en esta colección que incluye Uncharted 4: El Desenlace del Ladrón y Uncharted: El Legado Perdido, con gráficos mejorados y todos los DLC incluidos.'),
    ('Dragons Dogma 2 PS5', 'dd2.jpg', 79, 'Embárcate en una emocionante aventura de fantasía en el mundo de Gransys en esta esperada secuela de Dragons Dogma, donde explorarás un vasto mundo lleno de peligros y misterios, ¡con combates épicos y criaturas imponentes!'),
    ('Grand Theft Auto V PS5', 'gta5.jpg', 39, 'Sumérgete en el mundo del crimen y la corrupción en Los Santos, en esta versión mejorada y expandida de uno de los juegos más aclamados de todos los tiempos, con gráficos mejorados y contenido adicional exclusivo para la próxima generación.');